# Define the two split_zeitpunkt functions for mean and median split_zeitpunkt_mean <- function(data) { data %>% pivot_wider(id_cols = c(ID, Standort, Gruppe, Sex, Age_start, CogTel), # no doublets! names_from = Zeitpunkt, # Zeitpunkt long -> wide values_from = Mean) }
split_zeitpunkt_median <- function(data) { data %>% pivot_wider(id_cols = c(ID, Standort, Gruppe, Sex, Age_start, CogTel), # no doublets! names_from = Zeitpunkt, # Zeitpunkt long -> wide values_from = Median) }
# Run the function and check the first few rows ## BNDF bdnf_mean_wide <- split_zeitpunkt_mean(bdnf_mean) head(bdnf_mean_wide)
